import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Consumer } from "../context/context";
import TextInputGroup from "../layout/TextInputGroup";
import { fetchContactRequest } from "../requests/request";

interface EditContactState extends ContactProp {
  errors: { name: string; email: string; phone: string } | null;
}

export default function EditContact() {
  const params = useParams();
  const [state, setState] = useState<EditContactState>({
    id: "",
    name: "",
    email: "",
    phone: "",
    errors: null,
  });

  useEffect(async () => {
    const id = "1";
    const contact = await fetchContactRequest(id);

    if (contact !== undefined) {
      this.setState({
        name: contact.name,
        email: contact.email,
        phone: contact.phone,
      });
    }
  }, []);

  onSubmit = async (e: ChangeEventType, dispatch: DispatchType) => {
    e.preventDefault();

    const { name, email, phone } = state;

    // Check For Errors
    if (name === "") {
      setState({ errors: { name: "Name is required" } });
      return;
    }

    if (email === "") {
      setState({ errors: { email: "Email is required" } });
      return;
    }

    if (phone === "") {
      setState({ errors: { phone: "Phone is required" } });
      return;
    }

    const updContact = {
      name,
      email,
      phone,
    };

    // const { id } = this.props.match.params;

    const res = await axios.put(
      `https://jsonplaceholder.typicode.com/users/${"id"}`,
      updContact
    );

    dispatch({ type: "UPDATE_CONTACT", payload: res.data });

    // Clear State
    setState({
      name: "",
      email: "",
      phone: "",
      errors: {},
    });

    // this.props.history.push("/");
  };

  const handleChange = (e: ChangeEventType) =>
    setState({ [e.currentTarget.name]: e.currentTarget.value });

  const { name, email, phone, errors } = state;

  return (
    <Consumer>
      {(value) => {
        const { dispatch } = value;
        return (
          <div className="card mb-3">
            <div className="card-header">Edit Contact</div>
            <div className="card-body">
              <form onSubmit={(e) => onSubmit.bind(dispatch)}>
                <TextInputGroup
                  type="text"
                  label="Name"
                  name="name"
                  placeholder="Enter Name"
                  value={name}
                  onChange={handleChange}
                  error={errors?.name}
                />
                <TextInputGroup
                  label="Email"
                  name="email"
                  type="email"
                  placeholder="Enter Email"
                  value={email}
                  onChange={handleChange}
                  error={errors?.email}
                />
                <TextInputGroup
                  label="Phone"
                  name="phone"
                  type="number"
                  placeholder="Enter Phone"
                  value={phone}
                  onChange={handleChange}
                  error={errors?.phone}
                />
                <input
                  type="submit"
                  value="Update Contact"
                  className="btn btn-light btn-block"
                />
              </form>
            </div>
          </div>
        );
      }}
    </Consumer>
  );
}
